import React, { Component } from 'react';
import Node from './Node';

export default class PathFindVisualizer extends Component {
    constructor(props){
        super(props);
        this.state = {grid:[]};
    }

    createGrid(){
        let grid = [];
        for (let i = 0; i < this.props.i; i++) {
            let row = []
            for (let j = 0; j < this.props.j; j++) {
                row.push([])
            }
            grid.push(row)
        }
        this.setState({grid:grid})
    }

    render(){
        const {nodes} = this.state;
        return(
        <div className="my-4">
        {
          nodes.map((row,rowIdx)=>{
              return <div>
                  {row.map((node,nodeIdx) => <Node></Node>)}
              </div>
          })
        }
        </div>
        );
    }
}
