import React, { Component } from 'react';
import Node from './Node';
import { dijkstra } from '../algorithms/dijkstra';
const START_NODE_ROW = 8;
const START_NODE_COL = 20;
const FINISH_NODE_ROW = 5;
const FINISH_NODE_COL = 30;

export default class PathFindVisualizer extends Component {
    constructor(props){
        super(props);
        this.state = {grid:[]};
    }

    componentDidMount(){
        let grid = this.createGrid()
        this.setState({grid:grid})
    }

    animateDijkstra(visitedNodes,path){
        let animateGrid = () => {
            for(let i=0;i<visitedNodes.length;i++){
            setTimeout(()=>{
             const node = visitedNodes[i]
         const newGrid = this.state.grid.slice();
         const newNode = {
             ...node,
             isVisited:true
         };
         newGrid[node.row][node.col] = newNode
             this.setState({grid:newGrid});
            },30* i);
        }
       }
       animateGrid((path)=>{
        for(let j =0;j<path.length;j++){
            setTimeout(()=>{
                const node = path[j]
                const newGrid = this.state.grid.slice();
                const newNode = {
                    ...node,
                    isVisited:false,
                    isInPath:true
                };
                newGrid[node.row][node.col] = newNode
                    this.setState({grid:newGrid});

            },100*j)
           
        }
       })
    }

    visualizeDijkstra(){
       const {grid} = this.state
       const startNode = grid[START_NODE_ROW][START_NODE_COL]
       const finishNode = grid[FINISH_NODE_ROW][FINISH_NODE_COL]
       const [visitedNodes,path] = dijkstra(grid,startNode,finishNode)
       let grid1 = this.createGrid()
       this.setState({grid:grid1})
       this.animateDijkstra(visitedNodes,path)
       
    }

    createGrid(){
        let grid = [];
        for (let row = 0; row < this.props.i; row++) {
            let currentRow = []
            for (let col = 0; col < this.props.j; col++) {
                const currentNode = {
                    row,
                    col,
                    isStart:row===START_NODE_ROW && col=== START_NODE_COL,
                    isFinish:row===FINISH_NODE_ROW && col=== FINISH_NODE_COL,
                    distance:Infinity,
                    isVisited:false,
                    isWall:false,
                    isInPath:false,
                    parentNode:null
                }
                currentRow.push(currentNode)
            }
            grid.push(currentRow)
        }
        return grid
    }
    

    render(){
        const {grid} = this.state;
        let align = {
            textAlign:"center",
            fontSize:"0"
        }
        return(
        <>
        <button onClick={()=>this.visualizeDijkstra()}> Visualize Dijkstra's Algorithm</button>
        <div style={align} className="my-3">
        {
          grid.map((row,rowIdx)=>{
              return <div key={rowIdx}>
                  {row.map((node,nodeIdx) => {
                  const {isStart,isFinish,isVisited,isInPath} = node
                  return (<Node key={nodeIdx} isStart={isStart} isFinish={isFinish} isVisited={isVisited} isInPath={isInPath}></Node>)
                  })}
              </div>
          })
        }
        </div>
        </>
        );
    }
}
