import React, { Component } from 'react';
import Node from './Node';

export default class PathFindVisualizer extends Component {
    constructor(props){
        super(props);
        this.state = {nodes:[]};
    }

    componentDidMount(){
        let nodes = [];
        for (let row = 0; row < this.props.i; row++) {
            let currentRow = []
            for (let col = 0; col < this.props.j; col++) {
                const currentNode = {
                    row,
                    col,
                    isStart:row===10 && col===20,
                    isFinish:row===5 && col===50
                }
                currentRow.push(currentNode)
            }
            nodes.push(row)
        }
        this.setState({nodes:nodes})
    }

    render(){
        const {nodes} = this.state;
        let grid = {
            textAlign:"center",
            fontSize:"0"
        }
        console.log(nodes[0])
        return(
        <div style={grid} className="my-3">
        {
          nodes.map((row,rowIdx)=>{
              console.log(row)
              return <div key={rowIdx}>
                  {row.map((node,nodeIdx) => {
                  const {isStart,isFinish} = node
                  return (<Node key={nodeIdx} isStart={isStart} isFinish={isFinish}></Node>)
                  })}
              </div>
          })
        }
        </div>
        );
    }
}
